package learning;

public class SwitchTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =1;
		switch(i){
		case 1:
			System.out.println("i is 1");
			break;
		case 2:
			System.out.println("i is not 1");
			break;
			default:
			System.out.println("defualt");
			break;
		}
				
	}

}
